const sql = require('mssql');

const config = {
  user: 'sa',
  password: 'i@intech',
  server: 'localhost',
  database: 'ATM',
};

async function executeQuery(query) {
  let pool = await sql.connect(config);
  let result = await pool.request().query(query);
  return result;
}

module.exports = {
  executeQuery,
};
