import React, { useState, useContext } from "react";
import { MyContext } from "../../App";
import { Link } from "react-router-dom";
import './index.css';
import Logo from './logo.png';

export function Navbar() {
    const { isLogged, setIsLogged, isAdmin, setIsAdmin } = useContext(MyContext);

  function handleLog(e) {
    e.preventDefault();
    if (isLogged) {
      if (window.confirm("Are you sure you want to logout?")) {
        setIsLogged(false);
        localStorage.clear();
        window.location.href = "/";
      }
    } else if(!isLogged) {
        window.location.href = "/";
    }
  }

  return (
    <>
      <header>
        <nav>
          <img className="navbar-brand-logo" alt="logo" src={Logo} />
          <h1>AIRTRACK</h1>
          <div className="nav-links">
            <ul>
              <li><Link to="/home">HOME</Link></li>
              <li><Link to="/about">ABOUT</Link></li>
              { isLogged ?
              <>
              <li><Link to="/flight">FLIGHTS</Link></li>
              <li><Link to="/booking">BOOKING</Link></li>
              <li><Link to="/profile">PROFILE</Link></li>
              <li><Link to="/contact">CONTACT</Link></li>
              </>
                :
                <>
                <li><Link to="/contact">CONTACT</Link></li>
                <li><Link to="/signup">SIGNUP</Link></li>
                </>
              }
              <li><Link to="/" onClick={handleLog}>{isLogged ? "LOGOUT" : "LOGIN"}</Link></li>
            </ul>
          </div>
        </nav>
      </header>
    </>
  );
}
